package com.reactlibrary;

import android.app.Activity;
import android.content.Intent;
import android.os.Bundle;
import android.util.Log;

import androidx.annotation.Nullable;

import com.facebook.react.bridge.ActivityEventListener;
import com.facebook.react.bridge.Promise;
import com.facebook.react.bridge.ReactApplicationContext;
import com.facebook.react.bridge.ReactContextBaseJavaModule;
import com.facebook.react.bridge.ReactMethod;
import com.facebook.react.bridge.Callback;
import com.facebook.react.bridge.ReadableMap;
import com.cashfree.pg.CFPaymentService;
import com.facebook.react.bridge.ReadableType;

import org.json.JSONArray;
import org.json.JSONException;
import org.json.JSONObject;

import java.lang.reflect.Array;
import java.util.ArrayList;
import java.util.Collection;
import java.util.HashMap;
import java.util.List;
import java.util.Map;


public class RNPgReactNativeSdkModule extends ReactContextBaseJavaModule {

    private final ReactApplicationContext reactContext;
    CFPaymentService cfPaymentService;
    private Callback mCallback;
    private ReadableMap mParams;

    public RNPgReactNativeSdkModule(ReactApplicationContext reactContext) {
        super(reactContext);
        this.reactContext = reactContext;
        cfPaymentService = CFPaymentService.getCFPaymentServiceInstance();
        reactContext.addActivityEventListener(mActivityEventListener);
    }

    @Override
    public String getName() {
        return "RNPgReactNativeSdkBridge";
    }

    @ReactMethod
    public void startPaymentWEB(ReadableMap params, String stage, Callback callback) {
        Log.d("", "CF::SDK::Android::INVOKED");
        mCallback = callback;
        Activity currentActivity = getCurrentActivity();
        mParams = params;
        Map<String, String> paramsMap = toMap(params);
        if (!validate(paramsMap)) {
            return;
        }
        String token = getToken(paramsMap);
        String color1 = paramsMap.containsKey("color1") ? paramsMap.get("color1") : "#784BD2";
        paramsMap.remove("color1");
        String color2 = paramsMap.containsKey("color2") ? paramsMap.get("color2") : "#FFFFFF";
        paramsMap.remove("color2");
        boolean hideOrderId = paramsMap.containsKey("hideOrderId") && Boolean.parseBoolean(paramsMap.get("hideOrderId"));
        cfPaymentService.doPayment(currentActivity, paramsMap, token, stage, color1, color2, hideOrderId);
    }

    @ReactMethod
    public void startPaymentUPI(ReadableMap params, String stage, Callback callback) {
        Log.d("", "CF::SDK::Android::UPI::INVOKED");
        mCallback = callback;
        Activity currentActivity = getCurrentActivity();
        mParams = params;
        Map<String, String> paramsMap = toMap(params);
        if (!validate(paramsMap)) {
            return;
        }
        String token = getToken(paramsMap);
        cfPaymentService.upiPayment(currentActivity, paramsMap, token, stage);
    }

    @ReactMethod
    public void getUPIApps(final Promise promise) {
        cfPaymentService.getUpiClients(reactContext, new CFPaymentService.UPIAppsCallback() {
            @Override
            public void onUPIAppsFetched(ArrayList<HashMap<String, String>> upiAppList) {
                if (upiAppList.isEmpty()) {
                    promise.reject("404", "no UPI apps found.");
                } else {
                    promise.resolve(new JSONArray(upiAppList).toString());
                }
            }
        });
    }

    private boolean validate(Map<String, String> params) {
        params.put("source", "react-native-android");
        if (params.containsKey("tokenData")) {
            return true;
        } else {
            Log.e("CFSDK", "tokenData is not present in the params.");
            return false;
        }
    }

    private String getToken(Map<String, String> paramsMap) {
        return paramsMap.get("tokenData");
    }

    private String getJson(final Bundle bundle) {
        if (bundle == null) return null;
        JSONObject jsonObject = new JSONObject();

        for (String key : bundle.keySet()) {
            Object obj = bundle.get(key);
            try {
                jsonObject.put(key, wrap(bundle.get(key)));
            } catch (JSONException e) {
                e.printStackTrace();
            }
        }
        return jsonObject.toString();
    }

    public static Object wrap(Object o) {
        if (o == null) {
            return JSONObject.NULL;
        }
        if (o instanceof JSONArray || o instanceof JSONObject) {
            return o;
        }
        if (o.equals(JSONObject.NULL)) {
            return o;
        }
        try {
            if (o instanceof Collection) {
                return new JSONArray((Collection) o);
            } else if (o.getClass().isArray()) {
                return toJSONArray(o);
            }
            if (o instanceof Map) {
                return new JSONObject((Map) o);
            }
            if (o instanceof Boolean ||
                    o instanceof Byte ||
                    o instanceof Character ||
                    o instanceof Double ||
                    o instanceof Float ||
                    o instanceof Integer ||
                    o instanceof Long ||
                    o instanceof Short ||
                    o instanceof String) {
                return o;
            }
            if (o.getClass().getPackage().getName().startsWith("java.")) {
                return o.toString();
            }
        } catch (Exception ignored) {
        }
        return null;
    }

    public static JSONArray toJSONArray(Object array) throws JSONException {
        JSONArray result = new JSONArray();
        if (!array.getClass().isArray()) {
            throw new JSONException("Not a primitive array: " + array.getClass());
        }
        final int length = Array.getLength(array);
        for (int i = 0; i < length; ++i) {
            result.put(wrap(Array.get(array, i)));
        }
        return result;
    }

    private final ActivityEventListener mActivityEventListener = new ActivityEventListener() {

        @Override
        public void onActivityResult(Activity activity, int requestCode, int resultCode, Intent data) {
            if (requestCode == CFPaymentService.REQ_CODE && data != null && mCallback != null) {
                String responseString = getJson(data.getExtras());
                sendResult(responseString);
                Log.d("CF::SDK::Android", "Response : " + responseString);
            }
        }

        @Override
        public void onNewIntent(Intent intent) {

        }

    };

    private void sendResult(String response) {
        mCallback.invoke(response);
        mCallback = null; // to make sure callback is only invoked once.
    }

    public static Map<String, String> toMap(@Nullable ReadableMap readableMap) {
        if (readableMap == null) {
            return null;
        }

        com.facebook.react.bridge.ReadableMapKeySetIterator iterator = readableMap.keySetIterator();
        if (!iterator.hasNextKey()) {
            return null;
        }

        Map<String, String> result = new HashMap<>();
        while (iterator.hasNextKey()) {
            String key = iterator.nextKey();
            String value = toObject(readableMap, key);
            if (value != null) {
                result.put(key, value);
            }
        }

        return result;
    }

    public static String toObject(@Nullable ReadableMap readableMap, String key) {
        if (readableMap == null) {
            return null;
        }

        String result = null;
        if (readableMap.getType(key) == ReadableType.Boolean) {
            result = String.valueOf(readableMap.getBoolean(key));
        }
        if (readableMap.getType(key) == ReadableType.String) {
            result = readableMap.getString(key);
        }
        return result;
    }
}
